import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { FormGroup, FormBuilder, Validators, FormControl } from '@angular/forms';

@Component({
  selector: 'app-pharmamedicine',
  templateUrl: './pharmamedicine.component.html',
  styleUrls: ['./pharmamedicine.component.css']
})
export class PharmamedicineComponent {

  medicine: any[] = [];
  isShowForm:boolean = false

  medicineForm!: FormGroup;

  constructor(private formBuilder: FormBuilder,private http: HttpClient) {}
  onSubmit() {
    console.log(this.medicineForm.value);
  }

  deleted: number = -1;


  onStore(){
    console.log('form data = ',this.medicineForm.value);
     this.http.post("http://localhost:1111/newMedicine", this.medicineForm.value)
      .subscribe(response => {
        console.log(response);
      }, error => {
        console.error('Error:', error);
      });
  }
  ngOnInit(): void {
    this.fetchMedicineData();
    this.medicineForm = this.formBuilder.group({
      medicineName: new FormControl('',[Validators.required]),
      dosage:  new FormControl('',[Validators.required]) ,
      price:  new FormControl('',[Validators.required]),
      gst: new FormControl(18,[Validators.required]),
      discount: new FormControl(10,[Validators.required]),
      finalPrice: new FormControl('',[Validators.required]),
    });
  }

  fetchMedicineData(): void {
    this.http.get<any[]>("http://localhost:1111/Medicine")
      .subscribe(response => {
        this.medicine = response;
      }, error => {
        console.error('Error:', error);
      });
  }

  addMethod(): void {
    this.isShowForm = true
  }

  newMedicine(): void {


    // this.http.post("http://localhost:1111/newMedicine", send)
    //   .subscribe(response => {
    //     console.log(response);
    //   }, error => {
    //     console.error('Error:', error);
    //   });


  }

  edit(i: number): void {

  }

  save(): void {
    const send = {

    };

    this.http.put("http://localhost:1111/updatemedicine", send)
      .subscribe(response => {
        console.log(response);
      }, error => {
        console.error('Error:', error);
      });


  }

  delete(i: number): void {
    this.deleted = i;
    const medicineName = this.medicine[i].medicineName;

    this.http.delete(`http://localhost:1111/deletemedicine/${medicineName}`)
      .subscribe(response => {
        console.log(response);
      }, error => {
        console.error('Error:', error);
      });

    this.deleted = -1;
  }
}
