import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-frontofficeprofile',
  templateUrl: './frontofficeprofile.component.html',
  styleUrls: ['./frontofficeprofile.component.css']
})
export class FrontofficeprofileComponent implements OnInit {
  frontOfficeEmail: string | null = null;
  frontOfficeDetails: any = {};
  errorMessage: string | null = null;

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    // Retrieve email from session storage
    this.frontOfficeEmail = sessionStorage.getItem('loggedInUserEmail');

    if (this.frontOfficeEmail) {
      // Get front office details using the stored email
      this.getFrontOfficeDetails();
    } else {
      this.errorMessage = 'No logged-in user found';
    }
  }

  getFrontOfficeDetails(): void {
    this.http.get(`http://localhost:1111/getFrontOfficeByEmail/${this.frontOfficeEmail}`)
      .subscribe(
        (response: any) => {
          this.frontOfficeDetails = response.frontOffice;
        },
        (error: any) => {
          this.errorMessage = error.error.message || 'Something went wrong.';
        }
      );
  }
}
