import { Component } from '@angular/core';

@Component({
  selector: 'app-doctorlist',
  templateUrl: './doctorlist.component.html',
  styleUrls: ['./doctorlist.component.css']
})
export class DoctorlistComponent {

    hovered: number | null = null;
    searchTerm: string = '';
    filteredDoctors: any[] = [];
  
    indianDoctors = [
      // ... (Your doctor data remains unchanged)
    ];
  
    pageStyle = {
      'background-image': 'url("https://img.freepik.com/free-vector/abstract-medical-wallpaper-template-design_53876-61802.jpg?w=740&t=st=1703872185~exp=1703872785~hmac=de890d05056ce0c08b0cb0c62bfea578c1f6357b8294318ca27cffe68e736610")',
      'min-height': '100vh',
      display: 'flex',
      'flex-direction': 'column',
      'align-items': 'center',
    };
  
    titleStyle = {
      'font-size': '32px',
      'font-weight': 'bold',
      margin: '20px 0',
    };
  
    searchContainerStyle = {
      width: '100%',
      display: 'flex',
      'justify-content': 'flex-end',
      'margin-bottom': '20px',
    };
  
    searchInputStyle = {
      padding: '10px',
      width: '30%',
      'margin-right': '10px',
      border: '2px solid #007bff',
      height: '40px',
    };
  
    searchButtonStyle = {
      padding: '10px 20px',
      'background-color': '#007bff',
      color: 'white',
      border: 'none',
      'border-radius': '5px',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
      height: '40px',
      display: 'flex',
      'align-items': 'center',
    };
  
    cardContainerStyle = {
      display: 'flex',
      'flex-wrap': 'wrap',
      'justify-content': 'space-around',
    };
  
    cardStyle = {
      border: '5px solid #ccc',
      'border-radius': '5px',
      padding: '20px',
      margin: '10px',
      width: '300px',
      'text-align': 'left',
      transition: 'background-color 0.3s ease',
      cursor: 'pointer',
      position: 'relative',
      overflow: 'hidden',
    };
  
    doctorPhotoStyle = {
      width: '100%',
      'height': 'auto',
      'border-radius': '5px 5px 0 0',
    };
  
    hoverStyle = {
      'background-color': '#f0f0f0',
      transform: 'scale(1.05)',
    };
  
    buttonStyle = {
      padding: '10px 20px',
      'background-color': '#007bff',
      color: 'white',
      border: 'none',
      'border-radius': '0 0 5px 5px',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
      width: '100%',
      'margin-top': '10px',
    };
buttonHovered: any;
  
    handleSearch() {
      this.filteredDoctors = this.indianDoctors.filter(doctor =>
        doctor.name.toLowerCase().includes(this.searchTerm.toLowerCase())
      );
    }
  
    setHovered(doctorId: number | null) {
      this.hovered = doctorId;
    }
  }
