import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-viewall',
  templateUrl: './viewall.component.html',
  styleUrls: ['./viewall.component.css']
})
export class ViewallComponent implements OnInit  {

  datas: any[] = [];
  Approved: string = "Approved";
  Pending: string = "Pending";
  search: string = "";
  approvedList:any[] = []

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.loadData();
  }

  loadData(): void {
    this.http.get<any[]>('http://localhost:1111/appoinment').subscribe(
      (response) => {
        console.log("im in");
        this.datas = response;
        console.log('response = ',response);
        this.approvedList = response.filter(d=> d.status === true)
        console.log('this.approvedList = ',this.approvedList);
        
        
      },
      (error) => {
        console.error('Error:', error);
      }
    );
  }

  searchChange(): void {
    this.loadData();
  }
}
<ul class="nav nav-tabs" id="myTabs">
  <li class="nav-item">
      <a class="nav-link " id="fapprove" data-toggle="tab" href="fapprove">Appprove Patient</a>
  </li>
  <li class="nav-item">
      <a class="nav-link" id="fbook" data-toggle="tab" href="fbook">Book an Appointment</a>
  </li>
  <li class="nav-item">
    <a class="nav-link active" id="viewall" data-toggle="tab" href="viewall">Patient List</a>
</li>
<li class="nav-item">
  <a class="nav-link" id="Profile" data-toggle="tab" href="frontofficeprofile">Profile</a>
</li>
  <li class="nav-item">
      <a class="nav-link" id="logoutTab" href="">Logout</a>
  </li>
</ul>


<!-- <input class="search" placeholder="search" [(ngModel)]="search" (ngModelChange)="searchChange()" /> -->
<div style="background-image: ;"></div>
<ng-container *ngIf="datas.length > 0; else noData">
  <table class="styled-table">
    <thead>
      <tr>
        <th align="left">Appointment ID</th>
        <th align="left">Doctor Name</th>
        <th align="left">Patient Name</th>
        <th align="left">Description</th>
        <th align="left">Status</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let row of approvedList">
        <td align="left">{{ row.appointmentId }}</td>
        <td align="left">{{ row.doctorName }}</td>
        <td align="left">{{ row.patientName }}</td>
        <td align="left">{{ row.description }}</td>
        <td align="left" class="approved-status">Approved</td>
      </tr>
    </tbody>
  </table>
</ng-container>

<ng-template #noData>
  <p>No data available.</p>
</ng-template>
