import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-pharmamedicine',
  templateUrl: './pharmamedicine.component.html',
  styleUrls: ['./pharmamedicine.component.css']
})
export class PharmamedicineComponent {

  medicine: any[] = [];
  medicineName: string = "";
  dosage: string = "";
  gst: string = "";
  price: string = "";
  discount: string = "";
  search: string = "";
  add: number = -1;
  editedIndex: number = -1; // Rename 'edit' to 'editedIndex'

  deleted: number = -1;

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.fetchMedicineData();
  }

  fetchMedicineData(): void {
    this.http.get<any[]>("http://localhost:1111/Medicine")
      .subscribe(response => {
        this.medicine = response;
      }, error => {
        console.error('Error:', error);
      });
  }

  addMethod(): void {
    this.add = 1;
  }

  newMedicine(): void {
    const send = {
      dosage: this.dosage,
      medicineName: this.medicineName,
      price: this.price,
      gst: this.gst,
      discount: this.discount
    };

    this.http.post("http://localhost:1111/newMedicine", send)
      .subscribe(response => {
        console.log(response);
      }, error => {
        console.error('Error:', error);
      });

    this.medicineName = "";
    this.discount = "";
    this.dosage = "";
    this.price = "";
    this.gst = "";
    this.add = -1;
  }

  edit(i: number): void {
    this.editedIndex = i; // Change 'edit' to 'editedIndex'
    this.medicineName = this.medicine[i].medicineName;
    this.discount = this.medicine[i].discount;
    this.dosage = this.medicine[i].dosage;
    this.price = this.medicine[i].price;
    this.gst = this.medicine[i].gst;
  }

  save(): void {
    const send = {
      dosage: this.dosage,
      medicineName: this.medicineName,
      price: this.price,
      gst: this.gst,
      discount: this.discount
    };

    this.http.put("http://localhost:1111/updatemedicine", send)
      .subscribe(response => {
        console.log(response);
      }, error => {
        console.error('Error:', error);
      });

    this.medicineName = "";
    this.discount = "";
    this.dosage = "";
    this.price = "";
    this.gst = "";
    this.editedIndex = -1; // Change 'edit' to 'editedIndex'
  }

  delete(i: number): void {
    this.deleted = i;
    const medicineName = this.medicine[i].medicineName;

    this.http.delete(`http://localhost:1111/deletemedicine/${medicineName}`)
      .subscribe(response => {
        console.log(response);
      }, error => {
        console.error('Error:', error);
      });

    this.deleted = -1;
  }
}
