import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-approve',
  templateUrl: './approve.component.html',
  styleUrls: ['./approve.component.css']
})
export class ApporveComponent implements OnInit {
  datas: any[] = [];

  constructor(private http: HttpClient) {}

  ngOnInit() {
    this.loadAppoinment();
  }

  loadAppoinment() {
    this.http.get<any[]>('http://localhost:1111/appoinment').subscribe(
      (response) => {
        console.log('im in');
        this.datas = response;
      },
      (error) => {
        console.error('Error:', error);
      }
    );
  }

  change(data: any): void {
    console.log('rftgyuiop');
    console.log(data);

    const send = {
      appointmentId: data.appointmentId,
      doctorName: data.doctorName,
      patientName: data.patientName,
      status: true,
      description: data.description
    };

    console.log(send);

    this.http.put('http://localhost:1111/updateAppoint', send).subscribe(
      (response) => {
        console.log(response);
      },
      (error) => {
        console.error('Error:', error);
      }
    );

    // Assuming you want to reload the data after the change
    this.loadAppoinment();
  }
}
<body>
  <ul class="nav nav-tabs" id="myTabs">
    <li class="nav-item">
      <a class="nav-link" id="doctorlist" data-toggle="tab" href="doctorlist"
        >Doctors List</a
      >
    </li>
    <li class="nav-item">
      <a class="nav-link active" id="fapprove" data-toggle="tab" href="fapprove"
        >Appprove Patient</a
      >
    </li>
    <li class="nav-item">
      <a class="nav-link" id="fbook" data-toggle="tab" href="fbook"
        >Book an Appointment</a
      >
    </li>
    <li class="nav-item">
      <a class="nav-link" id="viewall" data-toggle="tab" href="viewall"
        >Patient List</a
      >
    </li>
    <li class="nav-item">
      <a
        class="nav-link"
        id="Profile"
        data-toggle="tab"
        href="frontofficeprofile"
        >Profile</a
      >
    </li>
    <li class="nav-item">
      <a class="nav-link" id="logoutTab" href="">Logout</a>
    </li>
  </ul>
  <div class="table-container">
    <table class="medicine-table">
      <thead>
        <tr>
          <th>appointmentId</th>
          <th>doctorName</th>
          <th>patientName</th>
          <th>description</th>
          <th>status</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let row of datas">
          <td>{{ row.appointmentId }}</td>
          <td>{{ row.doctorName }}</td>
          <td>{{ row.patientName }}</td>
          <td>{{ row.description }}</td>
          <td>
            <button *ngIf="!row.status" (click)="change(row)">Approve</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</body>
