// appointment.component.ts

import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-appointment',
  templateUrl: './appointment.component.html',
  styleUrls: ['./appointment.component.css']
})
export class AppointmentComponent implements OnInit {
  datas: any[] = [];
row: any;

  constructor(private http: HttpClient) {}

  ngOnInit() {
    this.loadAppointments();
  }

  loadAppointments() {
    this.http.get<any[]>('http://localhost:1111/appoinment').subscribe(
      (response) => {
        console.log("im in");
        this.datas = response.filter(row => row.status);
      },
      (error) => {
        console.error('Error:', error);
      }
    );
  }
}
<!-- doctor-appointment.component.html -->
<ul class="nav nav-tabs" id="myTabs">
  <li class="nav-item">
      <a class="nav-link active" id="docappointment" data-toggle="tab" href="docappointment">Appointment</a>
  </li>
  <li class="nav-item">
      <a class="nav-link" id="docmedicine" data-toggle="tab" href="docmedicine">Medicine</a>
  </li>
  <li class="nav-item">
      <a class="nav-link" id="" href="">Logout</a>
  </li>
</ul>

<div>


  <table>
    <thead>
      <tr>
        <th>appointmentId</th>
        <th>doctorName</th>
        <th>patientName</th>
        <th>description</th>
        <th>status</th>
      </tr>
    </thead>
    <tbody>
      <ng-container *ngFor="let row of datas">
        <tr *ngIf="row.status">
          <td>{{ row.appointmentId }}</td>
          <td>{{ row.doctorName }}</td>
          <td>{{ row.patientName }}</td>
          <td>{{ row.description }}</td>
          <td>{{ row.status ? 'Approved' : 'Pending' }}</td>
        </tr>
      </ng-container>
    </tbody>
  </table>
</div>
