import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.css']
})
export class BookComponent {
  form: FormGroup;
  err: string | null = null;

  constructor(private fb: FormBuilder, private router: Router, private http: HttpClient) {
    this.form = this.fb.group({
      name: ['', Validators.required],
      Doctor: ['', Validators.required],
      Dis: ['', Validators.required]
    });
  }

  save(): void {
    if (this.form.valid) {
      this.http.get<any[]>('http://localhost:1111/appoinment').subscribe(
        (response) => {
          const id = response.length;
          const data = {
            appointmentId: id + 1,
            doctorName: this.form.value.Doctor,
            patientName: this.form.value.name,
            status: false,
            description: this.form.value.Dis
          };

          this.http.post('http://localhost:1111/newAppoinment', data).subscribe(
            (response) => {
              console.log(response);
              this.router.navigate(['/Apporve']);
            },
            (error) => {
              console.error('Error:', error);
            }
          );
        },
        (error) => {
          console.error('Error:', error);
        }
      );
    } else {
      this.handleError('Please fill in all required fields.');
    }
  }

  hideSpam(): void {
    this.err = null;
  }

  handleError(message: string): void {
    this.err = message;
    setTimeout(() => this.hideSpam(), 1000);
  }

  isFormValid(): boolean {
    return this.form.valid;
  }
}
