import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
@Component({
  selector: 'app-doctorlogin',
  templateUrl: './doctorlogin.component.html',
  styleUrls: ['./doctorlogin.component.css']
})
export class DoctorLoginComponent implements OnInit {
  email: string = '';
  password: string = '';
  onRecaptchaChange: boolean = false;

  constructor(private router: Router, private http: HttpClient) {}

  ngOnInit(): void {}

  login(): void {
    if (this.onRecaptchaChange) {
      if (this.email) {
        if (this.password) {
          const data = {
            email: this.email,
            password: this.password,
            role: 'Doctor'
          };

          this.http.post<any>('http://localhost:1111/officialEmail/', data)
            .subscribe(
              (response) => {
                if (response) {
                  // Assuming the response structure is similar to your React code
                  this.router.navigate(['/DoctorDashboard']);
                } else {
                  console.log('Invalid credentials');
                }
              },
              (error) => {
                console.error('Error:', error);
              }
            );
        } else {
          console.log('Enter Password');
        }
      } else {
        console.log('Enter Email');
      }
    } else {
      console.log('Verify Captcha');
    }
  }
}
<!-- doctor-login.component.html -->

<div class="lcontainer">
  <div class="login">
    <h1>LOGIN</h1>
    <p>Email</p>
    <input type="email" [(ngModel)]="email" placeholder="Enter value" />
    <p>Password</p>
    <input type="password" [(ngModel)]="password" placeholder="Enter Password" />
    <a routerLink="/DoctorForgotPassword">
      <p class="fpass">forgot password ?</p>
    </a>
    <re-captcha class="ReCAPTCHA" (change)="onRecaptchaChange = true"></re-captcha>

    <div class="div1">
      <button (click)="login()">Login</button>
      <a routerLink="/DoctorRegister">
        <button>Register</button>
      </a>
    </div>

    <p *ngIf="error" class="error">{{ error }}</p>
  </div>
</div>
