import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-apointment',
  templateUrl: './apointment.component.html',
  styleUrls: ['./apointment.component.css']
})
export class ApointmentComponent {
  name: string = '';
  Doctor: string = '';
  Dis: string = '';
  err: string | null = '';

  constructor(private router: Router, private http: HttpClient) { }

  ngOnInit(): void {}

  save(): void {
    if (this.name) {
      if (this.Doctor) {
        if (this.Dis) {
          this.http.get<any>('http://localhost:1111/appoinment').subscribe(
            response => {
              const id = response.length;
              const data = {
                appointmentId: id + 1,
                doctorName: this.Doctor,
                patientName: this.name,
                status: false,
                description: this.Dis
              };

              this.http.post('http://localhost:1111/newAppoinment', data).subscribe(
                () => {
                  console.log('Data saved successfully');
                  this.router.navigate(['/userappointment']);
                },
                error => {
                  console.error('Error:', error);
                }
              );
            },
            error => {
              console.error('Error:', error);
            }
          );
        } else {
          this.showError('Enter Dis');
        }
      } else {
        this.showError('Enter Doctor name');
      }
    } else {
      this.showError('Enter proper name');
    }
  }

  hideError(): void {
    this.err = null;
  }

  private showError(message: string): void {
    this.err = message;
    setTimeout(() => this.hideError(), 1000);
  }
}

<ul class="nav nav-tabs" id="myTabs">
  <li class="nav-item">
      <a class="nav-link active" id="userappointment" data-toggle="tab" href="userappointment">Appointment</a>
  </li>
  <li class="nav-item">
      <a class="nav-link" id="usermedicine" data-toggle="tab" href="usermedicine">Medicine</a>
  </li>
  <li class="nav-item">
      <a class="nav-link" id="" href="">Logout</a>
  </li>
</ul>
<div class="login-container">
  <div class="login-form">

    <div class="form">
      <h1>BOOK</h1>

      <p>name</p>
      <input type="text" [(ngModel)]="name" placeholder="Enter Name" />

      <p>doctorName</p>
      <input type="text" [(ngModel)]="Doctor" placeholder="Enter Name" />

      <p>description</p>
      <input type="text" [(ngModel)]="Dis" class="description" />

      <button (click)="save()">Register</button>
      <p *ngIf="err" class="eror">{{ err }}</p>
    </div>
  </div>
