import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.css']
})
export class BookComponent {
  name: string = '';
  Doctor: string = '';
  Dis: string = '';
  err: string | null = null;

  constructor(private router: Router, private http: HttpClient) {}

  save(): void {
    if (this.name) {
      if (this.Doctor) {
        if (this.Dis) {
          this.http.get<any[]>('http://localhost:1111/appoinment').subscribe(
            (response) => {
              const id = response.length;
              const data = {
                appointmentId: id + 1,
                doctorName: this.Doctor,
                patientName: this.name,
                status: false,
                description: this.Dis
              };

              this.http.post('http://localhost:1111/newAppoinment', data).subscribe(
                (response) => {
                  console.log(response);
                  this.router.navigate(['/Apporve']);
                },
                (error) => {
                  console.error('Error:', error);
                }
              );
            },
            (error) => {
              console.error('Error:', error);
            }
          );
        } else {
          this.handleError('Enter description');
        }
      } else {
        this.handleError('Enter Doctor name');
      }
    } else {
      this.handleError('Enter proper name');
    }
  }
  handleError(arg0: string) {
    throw new Error('Method not implemented.');
  }

  hideSpam(): void {
    this.err = null;
  }
}
