import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

  name: string = '';
  Doctor: string = '';
  Dis: string = '';
  err: string | null = null;
email: any;
password: any;

  constructor(private router: Router, private http: HttpClient) {}

  save(): void {
    if (this.name) {
      if (this.Doctor) {
        if (this.Dis) {
          this.http.get<any[]>('http://localhost:1111/appoinment').subscribe(
            (response) => {
              const id = response.length;
              const data = {
                appointmentId: id + 1,
                doctorName: this.Doctor,
                patientName: this.name,
                status: false,
                description: this.Dis
              };

              this.http.post('http://localhost:1111/newAppoinment', data).subscribe(
                (response) => {
                  console.log(response);
                  this.router.navigate(['/Apporve']);
                },
                (error) => {
                  console.error('Error:', error);
                }
              );
            },
            (error) => {
              console.error('Error:', error);
            }
          );
        } else {
          this.handleError('Enter description');
        }
      } else {
        this.handleError('Enter Doctor name');
      }
    } else {
      this.handleError('Enter proper name');
    }
  }

  hideSpam(): void {
    this.err = null;
  }

  handleError(message: string): void {
    this.err = message;
    setTimeout(() => this.hideSpam(), 1000);
  }
}
